import React, { useState } from "react";
import "./login.css";
import logo from "../../assets/logo.png";
import facebook from "../../assets/facebook.png";
import google from "../../assets/search.png";
import twitter from "../../assets/twitter.png";

function Login() {
  const [state, setstate] = useState(false);
  const [name,setName] = useState('');
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');


  const handleClick = (e) => {
    e.preventDefault();

  };

  return (
    <div className="background">
      <div
        className={`container ${state ? "right-panel-active" : ""}`}
        id="container"
      >
        <div className="form-container sign-up-container">
          <img src={logo} className="logo" alt="logo" />
          <h3>Hi Let's Connect !!</h3>
          <form onSubmit={handleClick}>
            <h1>Create Account</h1>
            <input type="text" placeholder="Name" value={name} onChange={(e) => {setName(e.target.value)}} />
            <input type="email" placeholder="Email" value={email} onChange={(e) => {setEmail(e.target.value)}} />
            <input type="password" placeholder="Password" value={password} onChange={(e) => {setPassword(e.target.value)}} />
            <button>Sign Up</button>
            <span>Or SignUp using:</span>
            <div className="social-container">
              <img src={google} alt="Google" className="icons" />
              <img src={facebook} alt="Facebook" className="icons" />
              <img src={twitter} alt="Twitter" className="icons" />
            </div>
          </form>
        </div>
        <div className="form-container sign-in-container">
          <img src={logo} className="logo" alt="logo" />
          <h3>Hi Let's Connect !!</h3>
          <form onSubmit={handleClick}>
            <h1>Sign in</h1>

            <input type="email" placeholder="Email" value={email} onChange={(e) => {setEmail(e.target.value)}} />
            <input type="password" placeholder="Password" value={password} onChange={(e) => {setPassword(e.target.value)}} />
            <a href="/home">Forgot your password?</a>
            <button>Sign In</button>
            <span>Or SignIn using:</span>
            <div className="social-container">
              <img src={google} alt="Google" className="icons" />
              <img src={facebook} alt="Facebook" className="icons" />
              <img src={twitter} alt="Twitter" className="icons" />
            </div>
          </form>
        </div>
        <div className="overlay-container">
          <div className="overlay">
            <div className="overlay-panel overlay-left">
              <h1>Welcome Back!</h1>
              <p>
                To keep connected with us please login with your personal info
              </p>
              <button
                className="ghost"
                id="signIn"
                onClick={() => {
                  setstate((prev) => {
                    return !prev;
                  });
                }}
              >
                Sign In
              </button>
            </div>
            <div className="overlay-panel overlay-right">
              <h1>Hello, Friend!</h1>
              <p>Enter your personal details and start journey with us</p>
              <button
                className="ghost"
                id="signUp"
                onClick={() => {
                  setstate((prev) => {
                    return !prev;
                  });
                }}
              >
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
